INSERT INTO `recipe` (`id`, `name`, `image`, `description`, `steps`, `ingredients`, `created_at`, `updated_at`, `category_id`, `account_id`) VALUES
(1, 'Bí quyết nhỏ cho món thịt kho mềm tan trong suốt mà lại không ngấy', 'photo-1616223848235-16162238485391355061522.jpg', 'Thịt kho mềm ngọt với phần mỡ trong veo, chỉ cần nhìn thôi đã kích thích vị giác lắm rồi. Món ăn ngon miệng mà không hề ngấy chút nào.', 'Thịt rửa sạch thái khối vừa ăn.#Đổ thịt vào nồi với lượng nước thích hợp sau đó thêm hành lá, gừng thái lát đun sôi.# Khi nước sôi thì dùng muỗng hớt bỏ bọt.#Chần thịt khoảng 4 phút sau đó vớt ra rửa sạch lại bằng nước ấm.#Đun nóng chảo chống dính, cho thịt ba chỉ vào áp chảo, chiên cho đến khi thịt ba chỉ sém vàng là được (không thêm dầu ăn vào khi chiên).#Thêm nước tương đặc, nước tương nhạt vào đảo đều. Đậy nắp chảo đun nhỏ lửa trong 2 phút hoặc cho đến khi thịt lợn thấm đều màu.#Cho đường phèn, ô mai mơ cùng muối, lá nguyệt quế, hồi, bột nêm vị gà và 1 bát nước nóng cùng 120ml nước trà vào rồi đậy nắp nồi. Đun nhỏ lửa trong 20 phút.#Đun cho đến khi thịt cạn nước và nhừ thì tắt bếp. Lấy thịt ra đĩa là xong.', 'Thịt ba chỉ 600g#Đường phèn 5 viên nhỏ#Nước tương nhạt (Light soy sauce) 30ml#Nước tương đặc (Dark soy sauce) 15ml#Hành lá 3 cây#Gừng 4 lát#Hoa hồi 2 cái#Lá nguyệt quế 2 chiếc#Nước trà xanh 120ml', '2021-06-13 22:45:59', '2021-06-13 22:45:59', 1, 1),
(2, 'Dùng nồi cơm điện làm món sườn kho không dầu không ngán ngấy ăn với cơm là số 1!', 'suonkho.jpg', 'Nồi cơm điện là thiết bị nhà bếp với chức năng chính là nấu cơm, nhưng đây còn là một thiết bị vô cùng đa năng nếu bạn biết sử dụng đúng cách. Ví dụ như dùng nồi cơm điện làm món sườn kho thơm ngon, vừa đơn giản lại dễ dàng!', 'Sườn rửa sạch, chặt miếng vừa ăn. Cho sườn vào nồi nước, đun sôi khoảng 1 phút rồi vớt sườn ra rửa sạch lại.#Trong một bát tô lớn, cho xốt sườn nướng vào, thêm xốt thịt nướng, đường vào khuấy đều. Thêm nước tương, chút hạt tiêu và 1 bát con nước lọc vào khuấy đều.#Cho sườn đã chần qua vào nồi cơm điện, thêm nước xốt vừa pha vào, lượng xốt xâm xấp mặt sườn. Thêm gừng thái lát, hoa hồi, tỏi đập dập vào rồi đậy nắp nồi cơm điện, nhấn nút nấu (cook), chờ đến khi nồi cơm điện nhảy lên nút giữ ấm (warm) là được.', '2 dẻ sườn (khoảng 500g sườn)#2 thìa canh xốt sườn nướng#2 thìa canh xốt thịt nướng#60ml nước tương nhạt#15g đường#2 lát gừng#2 tép tỏi', '2021-06-13 22:52:49', '2021-06-13 22:52:49', 1, 1),
(3, 'Mỗi lần kho cá kiểu này là tôi phải nấu nhiều cơm, vì cả nhà ai cũng ăn khỏe hơn hẳn', 'cakho.png', 'Món cá kho thơm mùi gừng, thịt cá chắc ngọt, cách kho có chút giấm cho món cá kho không hề tanh. Chắc chắc sẽ rất hao cơm đấy!', 'Cá phi lê rửa sạch và thấm khô. Cắt cá thành những miếng dày 4cm. Gừng cạo vỏ cắt lát mỏng. Tỏi và hành tím bóc vỏ băm nhỏ. Hành lá cắt nhỏ hoặc cắt khúc tùy thích. Ớt tươi đập dập. Xà lách, cà chua và dưa leo ngâm nước muối loãng sau đó rửa sạch, để ráo nước. #Lấy nồi kho, làm nóng chút dầu ăn, cho tỏi và hành tím vào phi thơm. Tắt lửa. Tiếp tục thêm gừng cắt lát, ớt khô, ớt tươi và tất cả gia vị trên bài vào nồi khuấy tan đều. Cho cá vào, lật trở cá cho thấm gia vị. Ướp cá 30 phút.#Cá đã ướp xong. Đặt nồi lên bếp đun lửa vừa, trở mình cá để cá chín đều, thêm nước nóng vào và hạ nhỏ lửa kho cho cá thấm vị. #Lấy đĩa đặt thố cá lên, dọn kèm đĩa xà lách đã chuẩn bị. Dùng với cơm nóng hoặc cháo trắng đều ngon.', '500gr phi lê cá hồng (cá mú, cá lóc, cá hồi...)#1 lóng gừng lớn#3 tép tỏi#2 củ hành tím#2 trái ớt tươi, 3 trái ớt khô#Xà lách, cà chua, dưa leo ăn kèm', '2021-06-13 22:55:47', '2021-06-13 22:55:47', 1, 2),
(4, 'Luộc ngô chỉ mất 5 phút mà đảm bảo ngon quên lối về!', 'ngoluoc.png', 'Ngô luộc hay ngô hấp là một món ăn đã quá quen thuộc với chúng ta. Tuy nhiên, việc luộc ngô có vẻ mất khá nhiều thời gian nên nhiều chị thường ngại làm món này ở nhà. Thế nhưng có một cách làm chín ngô bằng lò vi sóng cực tiện lợi, các chị em cùng học ngay cách này nhé!', 'Các bạn có thể chọn ngô ngọt hay ngô nếp tùy vào sở thích. Ngô mua về nhớ để nguyên vỏ. Đặt ngô còn nguyên lớp vỏ vào lò vi sóng, để mức nhiệt cao nhất trong thời gian 3 phút. Sau đó cho ngô ra khỏi lò vi sóng, lưu ý lúc này ngô rất nóng nên bạn hãy dùng khăn để cầm vào bắp ngô nhé!#Dùng dao cắt một đoạn khoảng 3 - 4cm tính từ phần cuống bắp ngô. Sau đó dùng dĩa (nĩa) kéo bắp ngô ra khỏi lớp vỏ áo. Lúc này bắp ngô vẫn còn nóng nên bạn hãy dùng khăn để giữ bắp ngô.#Phết một lớp bơ lên bề mặt bắp ngô rồi rắc muối và hạt tiêu lên tùy khẩu vị là xong. Thưởng thức ngô khi còn nóng là ngon nhất.\r\n\r\n', 'Ngô sống còn nguyên vỏ (3 trái).#Bơ, muối, hạt tiêu', '2021-06-13 23:00:22', '2021-06-13 23:00:22', 2, 1),
(5, 'Thực đơn cơm tối mỗi món tối đa 10k nhưng không ai có thể chê được', 'rauluoc.jpg', 'Chỉ với chi phí chưa tới 10k mỗi món bạn sẽ có thực đơn cơm tối tươm tất tới không ngờ.\r\n', 'Rau muống mua về nhặt bỏ phần già, lá úa. Rửa sạch rau dưới vòi nước rồi để ráo.#Đun sôi một nồi nước ở lửa to, thả thêm một chút xíu muối vào.#Khi nước sôi kỹ thì bạn thả rau vào luộc. Lưu ý luộc ở lửa lớn và mở vung ra. Như vậy sẽ giúp rau xanh không bị úa.#Rau chín vớt ra rổ cho ráo nước rồi mới gắp vào đĩa. Lúc này thả sấu vào nồi nước luộc. Giảm nhỏ lửa, đợi sấu chín mềm thì tắt bếp. Để nguội nước canh.', '1 mớ rau muống#1 chút muối#3 – 4 quả sấu (có thể thay bằng quả me chua)', '2021-06-13 23:03:20', '2021-06-13 23:03:20', 2, 3),
(6, 'Đậu hũ mà chế biến theo cách này thì đảm bảo cả người lớn lẫn trẻ con đều mê', 'dauhuchien.gif', 'Vẫn là đậu hũ chiên nhưng làm theo cách mà chúng tôi gợi ý, bạn sẽ thấy thành phẩm vừa ngon vừa lạ, xuất sắc miễn chê!', 'Bạn rửa sạch 3 cây đậu hũ non rồi cắt thành từng khoanh tròn, mỗi khoanh dày khoảng 2cm. Cho phần lòng đỏ trứng muối vào bát và dùng dĩa dầm nhuyễn.#Bạn cho 100gr bột bắp ra bát. Tiếp theo, bắc chảo lên bếp, cho vào khoảng 2 thìa canh dầu ăn. Lăn từng miếng đậu hũ đã cắt nhỏ qua bát lòng đỏ trứng gà và bát bột bắp rồi thả vào chảo, chiên vàng các mặt. #Chiên xong, vớt đậu ra đĩa có lót sẵn giấy thấm dầu để loại bỏ dầu thừa.#Bạn cho vào chảo 1 thìa cà phê dầu ăn và phi thơm phần hành khô đã băm nhỏ. Khi hành dậy mùi thơm, đổ phần trứng muối đã dầm nhuyễn vào cùng 30-50ml sữa tươi không đường, 1/2 thìa cà phê hạt tiêu xay. Đảo đều cho đến khi hỗn hợp sôi lăn tăn.#Tiếp theo, bạn cho phần đậu hũ đã chiên vàng vào chảo trứng muối và om khoảng 2 phút để đậu ngấm trứng muối.#Bạn cho khoảng 1 thìa cà phê dầu ăn vào chảo, đợi dầu nóng già rồi đổ phần hành lá đã cắt nhỏ vào và đảo trong khoảng 1 phút.#Xếp phần đậu hũ sốt trứng muối ra đĩa, rưới phần hành lá đã đảo qua với dầu ăn lên trên. Cuối cùng, đặt 1 ít chà bông lên từng miếng đậu.\r\n\r\n', 'Đậu hũ non 3 cây#Lòng đỏ trứng muối 5-6 cái#Lòng đỏ trứng gà 2 cái#Sữa tươi không đường 30-50ml#Bột bắp 100gr#Hành lá 5-7 nhánh#Hành khô 2 củ', '2021-06-13 23:08:58', '2021-06-13 23:08:58', 4, 1),
(7, 'Nhà có nồi chiên không dầu mà không tận dụng để làm món bánh "siêu cấp ngon" này thì quá phí!', 'mut_man_chien.jpg', 'Mận hậu đang vào chính vụ, vì thế đây là thời điểm thích hợp để các chị em làm những món ngon từ mận. Mứt mận chính là một món ngon mà chúng ta không thể bỏ qua các chị em ạ. Hãy cùng học cách làm mứt mận bằng nồi chiên không dầu nhé!', 'Mận mua về rửa sạch với nước muối rồi để ráo. Chuẩn bị một âu nước muối pha loãng, khứa mận và ngâm trong nước muối khoảng 30 phút. Sau đó cho đường vào ngâm thêm ít nhất 5 tiếng đồng hồ. #Cho hỗn hợp mận vào chảo và sên cho đến khi mận chuyển màu hơi sẫm. Cho mận ra ngoài và giữ lại phần nước trong chảo để làm siro mận.#Gừng thái nhỏ hoặc giã nhỏ. Cho gừng vào cùng với mận và đảo trên bếp tầm 5 phút.#Cho mận vào nồi chiên không dầu và sấy ở nhiệt độ 100 độ C trong 60 phút. Đảo mận khoảng 2 lần trong lúc sấy để mận được sấy đều.#Sau đó, để mận qua đêm cho thật khô. Nếu trời nắng bạn có thể đem mận ra phơi nắng.', 'Mận hậu 1kg#Đường 400g#Gừng thái nhỏ 1 củ nhỏ', '2021-06-13 23:12:35', '2021-06-13 23:12:35', 4, 2),
(8, 'Nhà có cơm nguội không ăn hết các chị em hãy thử làm món cơm viên chiên bằng nồi chiên không dầu nhé!', 'com_vien_chien.jpg', 'Nhà còn nhiều cơm nguội mà chưa biết làm gì thì bên cạnh việc làm cơm rang, cơm cháy các chị em còn có thể làm món cơm viên chiên siêu ngon. Món ăn này vừa dễ lại có thể làm bằng nồi chiên không dầu rất tiện lợi.', 'Sơ chế nguyên liệu: Xúc xích, cà rốt, hành tây, hành lá thái nhỏ. Cho tất cả các nguyên liệu gồm cơm nguội, xúc xích, cà rốt, hành tây, hành lá, bột ngô, tương cà chua, tỏi băm, trứng gà vào bát và nêm nếm gia vị cho vừa miệng.#Trộn tất cả nguyên liệu sao cho tất cả dính đều vào nhau.#Tạo hình cơm thành các viên nhỏ: Dùng tay hoặc dùng màng bọc thực phẩm để nặn các viên cơm thành hình tròn nhỏ. Quết hoặc xịt dầu ăn đều lên bề mặt của các viên cơm.#Làm nóng nồi chiên không dầu ở nhiệt độ 180 độ C trong 5 phút. Cho cơm viên vào nồi chiên không dầu và set nhiệt độ 180 độ C trong 16 đến 20 phút. Trong thời gian đó bạn đảo mặt cơm để các viên cơm chín vàng đều. Với món cơm viên chiên này, các chị em hoàn toàn có thể biến tấu các nguyên liệu theo ý thích của mình. Khi ăn các chị em có thể chấm với xì dầu, tương cà hay tương ớt cũng đều rất ngon nhé!', 'Cơm nguội 1 bát con#Xúc xích 200g#Cà rốt nửa củ#Hành tây 1/4 củ#Bột ngô (hoặc bột gạo) 2 thìa canh#Tỏi băm 2 - 3 tép#Trứng gà 1 quả', '2021-06-13 23:16:37', '2021-06-13 23:16:37', 4, 3),
(9, 'Có một cách làm món rau củ trở nên thơm ngon hấp dẫn khiến cả nhà mê mẩn ăn một mạch hết bay!', 'banh_rau_cu_chien.jpg', 'Rất có thể món bánh rau củ chiên này sẽ khiến cả những người không thích ăn rau cũng phải thay đổi ý kiến.', 'Sơ chế nguyên liệu: Bí ngòi và cà rốt sơ chế sạch sau đó bào thành sợi mỏng. Đập trứng vào một bát khác rồi dùng đũa đánh tan.#Trộn nguyên liệu: Cho bí ngòi, cà rốt, chút muối và bột ngũ vị hương vào bát tô sau đó thêm trứng đã đánh tan và chút nước vào khuấy đều. Sau đó thêm từ từ bột mì vào.#Quét một lớp mỏng dầu ăn trong chảo, đun nóng rồi múc từng muỗng bột vào chiên từ từ ở lửa vừa cho đến khi bánh chín vàng một mặt thì lật mặt bánh, chiên chín mặt còn lại là xong.', 'Bí ngòi 1 quả#Trứng 2 quả#Cà rốt ½ củ#Bột mì 150g#Muối, bột ngũ vị hương, dầu ăn mỗi thứ một ít', '2021-06-14 14:01:04', '2021-06-14 14:01:04', 4, 3),
(10, 'Vải đang chín rộ nhưng làm thành món vừa lạ vừa ngon xuất sắc thế này thì 90% chúng ta chưa từng nghe qua', 'vai_chien.jpg', 'Mùa vải đến rồi các chị em ơi! Ngoài cách ăn trực tiếp hoặc làm món vải sấy khô, bạn còn có thể kết hợp vải với phô mai để tạo ra món ăn vặt đỉnh của chóp luôn. Trong bài viết này, chúng tôi sẽ vẽ đường đi nước bước chi tiết để chị em làm món ăn "thần sầu" này.', 'Rửa sạch quả vải. Dùng kéo/que xiên nhọn/ống hút to chọc vào phần cuống vải để tách bỏ hạt vải.# Với phô mai, chị em hãy cắt thành từng viên nhỏ, dạng hạt lựu.#Đập 2 quả trứng ra bát, dùng đũa hoặc phới lồng đánh cho lòng trắng và lòng đỏ quện vào nhau. Đổ bột mỳ và bột chiên xù ra 2 bát riêng. Bạn cho 1 viên phô mai đã cắt nhỏ vào lòng quả vải. Lặp lại thao tác trên cho đến khi hết phần vải đã chuẩn bị.#Tiếp theo, bạn lăn quả vải đã nhồi nhân phô mai vào trứng và bột theo thứ tự sau: Bát trứng - bát bột mỳ - bát bột chiên xù.#Sau khi lăn qua trứng và bột, cho 4-5 thìa canh dầu ăn vào chảo, đợi dầu nóng già rồi thả vải vào chiên vàng trên lửa vừa. Chiên xong, vớt vải ra 1 chiếc đĩa có lót sẵn giấy thấm dầu để loại bỏ dầu thừa.', 'Vải 400-500gr# Phô mai nguyên miếng 60gr#Bột mỳ, bột chiên xù 100gr mỗi loại#Trứng 2 quả', '2021-06-14 14:05:54', '2021-06-14 14:05:54', 4, 2),
(11, 'Mang ngay nồi chiên không dầu ra làm món vét tủ này cho bữa tối nay, đảm bảo cả nhà ai cũng thích!', 'trung-noi-chien-khong-dau.png', 'Nếu đã quá chán các món trứng luộc, trứng ốp la thì hội chị em giảm cân có thể thử món thịt bọc trứng chiên xem sao. Món ăn này vừa ngon lại vừa dễ làm khi có chiếc nồi chiên không dầu. Các chị em ăn món này vào bữa sáng hay bữa chính cũng đều rất hợp nhé! ', 'Các bạn có thể luộc trứng bằng nồi như bình thường hoặc có thể tận dụng luôn nồi chiên không dầu để làm trứng chín. Cho trứng vào nồi chiên không dầu set nhiệt độ 250 độ C trong 10 - 12 phút nếu muốn ăn trứng lòng đào, còn nếu muốn trứng chín hẳn thì để thời gian là 16 phút. Sau đó chuyển trứng vào bát nước đá.#Ướp thịt với các loại gia vị rồi sau đó bọc thịt ra ngoài trứng. Đánh tan một quả trứng gà rồi lần lượt cho thịt bọc trứng qua trứng gà sau đó lăn tiếp vào bột chiên xù.#Xịt dầu ăn vào thịt bọc trứng rồi cho vào nồi chiên không dầu. Set nhiệt độ 180 độ C trong 10 phút, sau đó lật lại và tiếp tục để thêm 10 phút nữa là được. Thành phẩm là những viên thịt bọc trứng thơm ngon với bên ngoài giòn rụm, bên trong thơm mềm.', 'Trứng gà 7 quả#Thịt heo xay 500g#Bột chiên xù 200g#Gia vị: Hạt nêm, nước mắm, hạt tiêu... nêm nếm tùy khẩu vị', '2021-06-14 14:16:27', '2021-06-14 14:16:27', 4, 3),
(12, 'Bữa tối cuối tuần có món sườn cháy tỏi thì cả nhà vui, con ăn với cơm, bố thì nhâm nhi cùng ly bia!', 'suon-chien-muoi-tieu.jpeg', 'Món sườn cháy tỏi cực kỳ hấp dẫn, phần tỏi chiên thơm lừng hòa quyện trong từng miếng sườn thịt ngọt và đậm đà là một món ăn từ sườn mà bạn không thể bỏ lỡ!', 'Sườn rửa sạch, chặt miếng vừa ăn rồi dùng giấy ăn thấm khô miếng sườn. Cho 50g nước, 2 nhánh tỏi, 1 củ hành khô, 2 lát gừng, một nhúm hành lá vào máy xay sinh tố, xay nhuyễn. Thêm 7g muối và phần nước vừa xay vào đảo đều, ướp trong vòng 15 phút.#Lấy 50g tỏi băm nhỏ, cho vào chảo dầu nóng 100 độ C, chiên trên lửa nhỏ đến khi chín vàng thì vớt tỏi ra để nguội.#Vẫn dùng chảo dầu tỏi vừa chiên đó, cho sườn đã ướp vào chảo dầu tỏi ở nhiệt độ 160 độ C dưới lửa vừa trong khoảng 5 phút. Chiên từ từ trong khoảng 5 phút. Khi chuyển màu hoàn toàn, vớt sườn đã ướp ra. Sau đó cho lửa to hơn khi dầu đạt 180 độ, cho sườn trở lại chảo và chiên ngập dầu trong 3 phút. Lúc này sườn đã chín hoàn toàn. Gắp sườn ra đĩa đã lót sẵn giấy nến.#rong một nồi khác, đổ một ít dầu đã chiên tỏi vào, thêm lượng thích hợp bột ngũ vị, muối, bột tiêu, ớt bột sau đó cho sườn vào đảo đều, cuối cùng thêm tỏi phi đảo lại lần nữa cho tỏi quyện vào với sườn. Cuối cùng xếp sườn ra đĩa rồi rắc một chút hành lá băm nhỏ lên là xong.', 'Sườn 700g#Muối 7g#Hành khô 1 củ#Gừng 2 lát#Tỏi 55g#Bột ngũ vị hương, tiêu, ớt bột, hành lá mỗi thứ một ít#', '2021-06-14 14:21:03', '2021-06-14 14:21:03', 4, 2),
(13, 'HƯỚNG DẪN NẤU ĂN NGON\r\nMách chị em cách nấu một loại canh chua vừa ngon, vừa dễ', 'canh_suon_chua.jpg', 'Món canh chua mà chúng tôi gợi ý cho chị em trong bài viết này chính là canh sườn nấu cùng cà chua và dọc mùng. Nếu đã chán ăn cơm, chị em chỉ cần nấu bát canh này và mua thêm ít bún, đảm bảo bữa ăn vẫn đơn giản mà vẫn ngon.', 'Sơ chế các nguyên liệu: Cà chua bạn rửa sạch, cắt bỏ phần cuống và thái thành từng miếng nhỏ. #Bắc 1 nồi nước lên bếp, đun sôi. Khi nước sôi sùng sục, thả sườn vào và chần sơ khoảng 2 phút cho đến khi miếng sườn săn lại thì vớt ra.#Tiếp theo, bạn cho vào nồi 1 thìa cà phê dầu ăn và phi thơm phần hành khô đã băm nhuyễn. Khi hành dậy mùi thơm, đổ phần sườn heo đã chần sơ vào và đảo khoảng 1 phút cho sườn sém cạnh.#Bạn cho khoảng 800-1000ml nước vào nồi cùng phần cà chua đã cắt nhỏ. Nêm thêm 1 thìa cà phê muối, 2 thìa cà phê hạt nêm, khuấy đều. Đậy vung, giảm lửa và đun canh khoảng 10 phút.#Sau 10 phút, bạn thêm phần dọc mùng đã sơ chế vào nồi cùng me hoặc sấu đã rửa sạch, đun thêm khoảng 2-3 phút nữa thì tắt bếp. Chị em có thể nêm nếm lại gia vị cho vừa miệng. \r\n\r\n', '400gr sườn heo#3 quả cà chua, 3 quả me hoặc sấu#4 nhánh dọc mùng#2 nhánh hành lá, 2 củ hành khô#Gia vị: Muối, hạt nêm', '2021-06-14 14:24:04', '2021-06-14 14:24:04', 5, 1),
(14, 'Người Hàn có món canh vừa ngon vừa mát, ăn vào mùa hè thì chuẩn không cần chỉnh!', 'canh_tao_be.jpg', 'Một món canh với vị hải sản mà cách chế biến vô cùng dễ như món canh tảo bẹ dưới đây thì nhất định bạn nên thử. Món canh có tác dụng giải nhiệt hiệu quả ngày hè.', 'Tảo bẹ rửa sạch sau đó ngâm nước cho nở ra, rồi rửa sạch lại.#Cắt tảo bẹ thành dải vừa ăn, đậu phụ thái khối, nấm sò rửa sạch rồi xé nhỏ.#Đun nóng chút dầu ăn trong chảo, cho gừng tỏi thái nhỏ vào xào thơm rồi đổ tảo bẹ vào xào trong 2 phút.#Thêm lượng nước nóng vừa ăn vào đun sôi, đổ nấm sò và đậu phụ vào đun trong 8 phút ở lửa vừa. Thêm chút muối, hạt tiêu cho vừa ăn rồi rắc chút hành lá thái nhỏ vào là xong.', 'Nấm sò 7 cây#Đậu phụ 1 miếng#Tảo bẹ khô 80g#Gừng, hành tỏi, hạt tiêu, muối mỗi thứ một ít.', '2021-06-14 14:26:15', '2021-06-14 14:26:15', 5, 1),
(15, 'Chẳng cần thêm thịt, món canh chua này vẫn cứ ngon xuất sắc!', 'canh_chua_chay.jpg', 'Ngày mai là mùng 1 tháng 4 âm lịch, nếu bạn có thói quen ăn chay vào ngày đầu tháng và ngày rằm, vậy thì món canh chua chay mà chúng tôi gợi ý trong bài viết này chính là điều bạn cần!', 'Đậu hũ rửa sạch, để ráo rồi cắt thành miếng vuông nhỏ vừa ăn. Các loại rau mùi, rau thơm và ớt sừng, chị em cũng rửa sạch rồi xắt lát nhỏ. Giá đỗ rửa sạch. Cà chua rửa sạch và bổ miếng cau. Để tiết kiệm thời gian, bạn hãy mua dứa gọt sẵn, về chỉ việc rửa lại và cắt thành miếng nhỏ là xong.#Với dọc mùng, bạn hãy rửa sạch rồi dùng dao tước vỏ và thái thành từng miếng vừa miệng. Sau đó, ngâm dọc mùng với nước muối pha loãng trong khoảng 20 - 30 phút để dọc mùng nhả hết nhựa.#Vớt dọc mùng ra rổ rồi bóp nhẹ tay với muối, đem ngâm lại với nước sạch 15 phút. Cẩn thận hơn, chị em có thể chần dọc mùng với nước sôi trong khoảng 2 phút nếu sợ bị ngứa lưỡi khi ăn. Đừng quên đeo găng tay để không bị ngứa nhé!#Cho khoảng 2 thìa cà phê dầu ăn vào nồi, đợi dầu nóng già rồi đổ dứa và cà chua vào, xào trong khoảng 1 phút. Nêm thêm 1 muỗng cà phê hạt nêm, 1 muỗng cà phê muối và đảo đều.#Tiếp theo, bạn đổ vào nồi khoảng 600ml nước cùng 2 quả me hoặc sấu, khuấy đều rồi đậy vung, vặn to lửa cho nồi canh sôi. Cuối cùng, thêm đậu hũ, giá đỗ và các loại rau thơm cùng ớt vào nồi. Nêm nếm lại cho vừa miệng, để canh sôi thêm khoảng 30 giây thì tắt bếp. Vậy là chị em đã nấu xong bát canh chua chay rồi đấy. Múc canh ra bát và thưởng thức thôi!', 'Đậu hũ 200gr#Giá đỗ 100gr#Dọc mùng 2 nhánh#Dứa 1/2 quả#Cà chua 2-3 quả#Me hoặc sấu 2-3 quả#Rau ngò gai, rau thơm, ớt sừng Một ít (số lượng tùy sở thích)', '2021-06-14 16:45:28', '2021-06-14 16:45:28', 5, 3),
(16, 'Có một loại chè ăn càng nhiều da càng đẹp, lại chẳng sợ tăng cân!', 'che-nha-dam.gif', 'Các loại chè nói chung là một trong những món ăn được rất nhiều chị em yêu thích vào mùa hè. Trời nóng, ăn chè vừa ngọt vừa mát rất phê! Tuy nhiên, nếu bạn đang ăn kiêng hoặc sợ rằng ăn nhiều chè sẽ gây tăng cân, nóng trong nổi mụn, vậy thì hãy vào bếp nấu ngay món chè cam - nha đam mà chúng tôi gợi ý sau đây, Với món chè này, chị em có thể yên tâm "chén" đến chán thì thôi!', 'Nha đam sau khi mua về, chị em hãy dùng dao tước bỏ phần vỏ xanh bên ngoài và cắt lấy phần thịt nha đam. Sau đó, rửa kỹ nha đam dưới vòi nước cho đến khi sờ phần thịt nha đam không còn cảm thấy nhơn nhớt nữa.Cắt nha đam thành miếng nhỏ, dạng hạt lựu.#Tiếp theo, bạn chuẩn bị 1 bát nước muối loãng và 1 bát nước đá lạnh. Ngâm phần nha đam vừa cắt trong bát nước muối loãng khoảng 5-7 phút, vớt ra rửa dưới vòi nước rồi tiếp tục ngâm trong bát nước đá khoảng 10 phút. Ngâm xong, vớt ra để ráo. Bạn gọt vỏ 1 quả cam vàng, có thể giữ lại một ít phần cùi trắng rồi cắt cam thành dạng hạt lựu#Cho phần cam đã cắt nhỏ vào nồi cùng 50gr đường ăn kiêng, đảo đều trên lửa nhỏ cho đến khi cam tiết ra nước và đường tan ra. Thêm khoảng 500ml nước vào nồi, đợi nước sôi rồi thả phần nha đam đã sơ chế vào, khuấy đều khoảng 30 giây rồi đun cho đến khi hỗn hợp sôi trở lại thì tắt bếp.#Sau khi tắt bếp, bạn đổ phần gelatin đã cắt nhỏ vào và khuấy thêm khoảng 30 giây.#Cuối cùng, đổ hỗn hợp vào hộp và để trong ngăn mát tủ lạnh khoảng 1-2 tiếng.#Hoàn thành món chè cam, nha đam: Bạn cho phần cam - nha đam đã ủ lạnh vào cốc, rót thêm nước cam vào, khuấy đều. ', 'Nha đam tươi 200gr#Cam vàng không hạt 1 quả#Đường ăn kiêng 50gr#Gelatin 5 miếng#Nước cam 300-400ml', '2021-06-14 16:53:50', '2021-06-14 16:53:50', 8, 1),
(17, '"Giải cứu" khoai lang tím với cách nấu chè khoai tím dẻo bùi hấp dẫn - ngon không thua đầu bếp chuyên nghiệp', 'che-khoai-tim.png', 'Nhà có quá nhiều khoai lang tím thì làm gì bây giờ? Luộc hay nướng mãi cũng chán, hãy thử cách nấu chè khoai tím dẻo bùi dưới đây nhé!', 'Khoai lang tím cắt khúc, cho vào nồi xâm xấp nước,thêm muối và rồi luộc trong 5 phút. Khoai chín thì vớt ra, dùng dĩa dầm nhuyễn. Cho bột vào âu khoai. Đổ nước sôi sùng sục vào, dùng dĩa trộn cho bớt nóng.#Cho nước cốt dừa vào âu khoai. Đeo găng tay nhào trộn bột thành 1 khối nhuyễn. Nặn thành từng viên hình tròn (to nhỏ tùy ý). Xếp ra đĩa.#Cho 100g bột năng cùng 5gr bột rau câu dẻo vào âu bột. Cho đường vào âu. Đổ nước sôi sùng sục vào âu bột, dùng dĩa trộn cho bớt nóng. Đeo găng tay ấn nhồi bột. Thấy khối bột mịn, không dính tay thì phủ thêm lớp bột áo. Vê bột thành sợi dài và dùng kéo cắt thành từng viên nhỏ. Đun 300gr nước sôi, thả trân châu vào luộc trong 3 phút. Vớt vào bát đã có 30gr đường. Dùng đũa trộn đều, đậy kín lại bằng màng bọc thực phẩm để ủ trân châu.#Cho 5gr rau câu dẻo vào bát nhỏ, đổ 100gr đường vào trộn đều. Cho bát hỗn hợp vừa trộn vào nồi có 300gr nước, trộn đều. Đun nóng đến khi thấy sôi lăn tăn thì cho nước cốt dừa vào khuấy đều. Đợi sôi hẳn thì nhấc ra khỏi bếp, đổ vào khuôn, đợi nguội thì cắt thạch theo hình tùy ý.#Đổ 1.100ml nước vào nồi, cho đường thốt nốt vào đun cho tan. Thả những viên khoai lang tím vào nồi nước. Luộc đến khi thấy các viên khoai nổi lên (khoảng 5 phút) là đã chín.#Lấy 50gr bột năng pha với 100ml nước. Vừa đổ vừa khuấy để bột không bị lắng xuống đáy nồi. Nước trong, sệt là đạt.', '#50g bột năng#30g bột nếp#150g khoai tím#20g nước cốt dừa#50g nước#100g bột năng#90g đường#5g bột rau câu dẻo#400g đường', '2021-06-14 17:00:32', '2021-06-14 17:00:32', 8, 1),
(18, 'Vải mà kết hợp với loại hạt quen thuộc này sẽ tạo ra món chè ngon "thần sầu", cách nấu thì vô cùng đơn giản!', 'che-vai-hat-sen.jpg', 'Những ngày gần đây, chắc hẳn bạn đều thấy vải thiều Bắc Giang/Thanh Hà được bán ở khắp mọi nơi, từ những điểm "giải cứu vải" đến chợ rồi siêu thị, đi đâu cũng thấy! Vải đã vào mùa thu hoạch, nếu chị em vừa tham gia giải cứu vải xong mà chưa biết làm sao để xử lý được 5-10kg vải, vậy thì hãy thử vào bếp nấu món chè vải - hạt sen mà chúng tôi gợi ý ngay sau đây.\r\n\r\n', 'Bạn rửa sạch 400gr vải, sau đó bóc vỏ từng quả và dùng dao/kéo/ống hút to khoét nhẹ vào phần cuống vải và loại bỏ hạt. Với hạt sen, nếu bạn dùng hạt sen tươi, vậy thì chỉ cần rửa qua hạt sen với nước. Nếu dùng hạt sen khô, bạn hãy ngâm hạt sen ngập trong nước khoảng 8 tiếng (qua đêm).#Bạn cho 50-70gr đường kính vào nồi cùng 800ml nước, vừa đun vừa khuấy cho đến khi hỗn hợp nước đường sôi sùng sục thì thả phần hạt sen đã sơ chế vào và đun trong 10 phút.#Tiếp theo, bạn hãy nhét vào mỗi quả vải 1 hạt sen vừa đun chín. Lặp lại thao tác này cho đến khi hết phần hạt sen đã chuẩn bị.#Bạn cho vào nồi nước đường vừa dùng để đun hạt sen 50-70gr đường phèn, vừa đun vừa khuấy đều cho đến khi hỗn hợp sôi lăn tăn thì thả phần vải bọc hạt sen vào nồi. Từ từ đổ bát nước bột năng đã pha, vừa đổ vừa khuấy nhẹ nhàng cho đến khi bạn thấy phần nước đã đạt được độ sánh như mong muốn thì dừng lại.#Vậy là bạn đã hoàn thành xong món chè vải - hạt sen rồi đấy! Đợi chè nguội bớt, múc ra bát, thêm ít nước cốt dừa và cùi dừa nạo vào nếu muốn, thế là có ngay bát chè vừa ngon vừa mát để thưởng thức.', 'Vải 400gr#Hạt sen 100gr#Đường nâu và đường kính 50-70gr mỗi loại#Bột năng 2-3 thìa cà phê#Nước cốt dừa, cùi dừa (Không bắt buộc) 1 ít', '2021-06-14 17:04:30', '2021-06-14 17:04:30', 8, 1),
(19, 'Đánh bay cái nóng chiều hè với món chè ngon - bổ - rẻ', 'che-sen.jpg', 'Các món chè ngon, ngọt thanh, tươi mát dưới đây chính là 1 trong những món ăn vặt "đập tan" cơn khát cháy bỏng mùa hè!', 'Đậu xanh tách vỏ, ngâm trong nước khoảng 2 tiếng, sau đó vo và đãi sạch.#Cho lá nha đam vào xả sạch. Sau đó lọc lấy phần trắng bên trong. Cắt đôi quả chanh và vắt nửa quả vào một tô nước sạch, pha vào đó 1 ít muối rồi cho ruột nha đam vừa tách vào ngâm. Khoảng 10 phút sau thì xả sạch với nước rồi xắt nhỏ thành hình hạt lựu. Cuối cùng đem nha đam vào ngâm trong 1 bát nước đá lạnh để nha đam được trắng, giòn và ngon hơn.#Nấu chè: Ninh mềm đậu xanh và hạt sen, Sau đó, cho cả hạt sen, đậu xanh và nha đam vào chung 1 nồi nấu cùng khoảng 10 phút, sau đó nêm thêm đường (tùy khẩu vị) và vani cho món chè đậm vị.\r\n\r\n', 'Hạt sen tươi: 500gr (có thể mua sẵn hạt đã lấy tim sen rồi)#Đường phèn: 200gr#Nha đam: 500gr#Đậu xanh: 200gr#Chanh: 1/2 quả#1 ống vani#', '2021-06-14 17:08:21', '2021-06-14 17:08:21', 8, 2),
(20, 'HƯỚNG DẪN NẤU ĂN NGON\r\nCó món chè tuổi thơ mà giờ tìm mỏi mắt cũng chẳng mấy chỗ bán: Muốn ăn chỉ có thể "lăn" vào bếp!', 'che-hat-ke.jpg', 'Trời nóng, ăn chè là chuẩn bài nhất. Thiên đường chè những ngày này có cả trăm cái tên cho chị em lựa chọn, nào là chè khúc bạch, chè xoài, chè bưởi,... Nhưng có một món chè tuổi thơ mà bây giờ chẳng mấy chỗ bán. Đó chính là món chè kê.', 'Bạn cho 200gr đậu xanh và 200gr hạt kê ra 2 bát riêng, đổ ngập nước và ngâm trong khoảng 2-3 tiếng. Ngâm xong, bạn đổ bát hạt kê và bát đậu xanh qua rây lọc để loại bỏ phần nước ngâm.#Đầu tiên, bạn rửa sạch 5-7 nhánh lá dứa và bó cuộn lại. Cho lá dứa đã rửa sạch vào nồi cùng 300-400ml nước và đun sôi. Sau đó, vớt lá dứa ra và đổ đậu xanh đã ngâm vào, đun trên lửa nhỏ trong khoảng 15 phút.#Tiếp theo, bạn cho phần đậu xanh cùng nước trong nồi vào máy xay sinh tố và xay nhuyễn.#Cho 600ml nước vào nồi cùng phần hạt kê đã ngâm nở, vừa đun vừa khuấy trên lửa lớn. Đến khi nồi kê sôi lăn tăn, bạn đổ bát đậu xanh đã xay nhuyễn vào nồi, giảm lửa và khuấy đều khoảng 30 giây.#Cuối cùng, bạn cho 100-150gr đường phèn vào nồi, khuấy đều rồi đun thêm khoảng 1 phút nữa.', 'Đậu xanh 200gr#Hạt kê 200gr#Đường phèn 100-150gr#Lá dứa 5-7 nhánh', '2021-06-14 19:39:44', '2021-06-14 19:39:44', 8, 2),
(21, 'Cách nấu chè chuối mới toanh chỉ mất 10 phút, ăn ngon mà khỏi sợ nóng nực đứng bếp!', 'che-chuoi.jpg', 'Một bát chè chuối yến mạch ngọt thơm mát lành chắc chắn là món ăn ngon miệng cho hội chị em giảm cân mà thèm ăn chè đấy!', 'Thái lát chuối: chuối bóc bỏ vỏ rồi thái thành lát vừa ăn.#Đổ lượng nước thích hợp vào nồi đun sôi ở lửa lớn, khi nước sôi thì cho yến mạch vào khuấy đều.#Tiếp đó đổ sữa tươi vào và đun sôi ở lửa vừa.#Khi sữa yến mạch sôi thì thêm chuối vào đun nhỏ lửa trong 1 phút.#Thêm đường phèn vào đun cho đường tan rồi tắt bếp.#Cho chè chuối yến mạch ra bát, thêm việt quất vào là xong.', 'Yến mạch ăn liền 50g#Chuối chín 1 quả#Sữa tươi 200ml#Việt quất 5 quả (hoặc loại trái cây khác mà bạn thích)#Đường phèn lượng thích hợp', '2021-06-14 19:42:28', '2021-06-14 19:42:28', 8, 2),
(22, 'Có một món chè nấu cực dễ mà ăn vừa thơm, vừa mát: Cuối tuần chị em hãy nấu ngay!', 'che-bap.png', 'Tranh thủ cuối tuần rảnh rang, chị em hãy vào bếp làm ngay món chè ngô lá dứa thơm ngon, thanh mát này ăn cho đã miệng.', 'Cho 150gr bột bắp ra bát và trộn với khoảng 90-100ml nước sôi để nguội. Bạn nên đổ từ từ nước vào bột, vừa đổ vừa trộn để tránh việc cho quá nhiều nước, khiến bột bị nhão.#Lấy 1 phần bột nếp đã nhào và đặt 1 hạt ngô vào rồi nặn thành viên bột tròn. Kích thước viên bột bọc ngô này không nên quá to, cũng không nên quá bé, kích thước tương đương 1 hòn bi ve là hợp lý.#Cho khoảng 700ml nước vào nồi và đun sôi. Khi nước sôi, bạn thả những viên bột bọc ngô vừa nặn vào và đun cho đến khi các viên bột nổi lên mặt nước, dùng muôi lỗ vớt viên bột và thả vào bát nước lạnh. Ngâm khoảng 2 phút thì vớt viên bột ra bát riêng.#Cho phần lá dứa đã rửa sạch và cắt nhỏ vào máy xay cùng khoảng 150-200ml nước và xay nhuyễn. Sau đó, lọc hỗn hợp thu được qua rây lọc để loại bỏ phần bã.#Cho phần ngô ngọt còn lại vào nồi cùng khoảng 600ml nước, đun sôi. Trong quá trình đun, nếu nồi nước nổi bọt, bạn hãy dùng thìa vớt hết bọt đi nhé! Sau đó, cho vào nồi 60gr đường kính hoặc đường nâu cùng phần nước cốt lá dứa, khuấy đều và để hỗn hợp sôi thêm khoảng 30 giây.#Tiếp theo, bạn đổ từ từ bát nước bột năng vào nồi, vừa đổ vừa khuấy đều. Sau đó, thả những viên bột bọc ngô vào và giảm lửa, nấu thêm khoảng 2 phút.#Vậy là chị em đã nấu xong món chè ngô - lá dứa rồi! Múc chè ra bát, thêm đá viên vào cho mát và thưởng thức thôi.', 'Ngô ngọt 1 hộp#Bột nếp 150gr#Bột năng 2-3 thìa cà phê#Lá dứa 2 nhánh#Đường kính hoặc đường nâu 60gr', '2021-06-14 19:45:56', '2021-06-14 19:45:56', 8, 3),
(23, 'Mách chị em cách nấu chè nhàn tênh bằng nồi cơm điện: Bấm nút vài lần là có ngay bát chè vừa ngon, vừa mát!', 'che-dua.jpg', 'Mùa hè ăn chè rất "đã" là sự thật ai cũng biết rồi, nhưng nhiều người vẫn ngại vào bếp vì nghe đến chuyện nấu chè, cảm giác thao tác chế biến khá cồng kềnh. Bài viết này của chúng tôi sẽ giúp chị em thổi bay cảm giác đó. ', 'Nếu dùng hạt sen tươi, bạn hãy rửa hạt sen với nước, không cần ngâm. Nếu dùng hạt sen khô, sau khi rửa, bạn ngâm hạt sen trong nước khoảng 8 tiếng (qua đêm). Đậu xanh cũng rửa sạch và ngâm ngập trong nước khoảng 8 tiếng (qua đêm). Bạn có thể chọn mua đậu xanh đã xát vỏ hoặc còn nguyên vỏ đều được. Với đậu xanh còn vỏ, sau khi ngâm, bạn hãy dùng tay vo mạnh một chút để phần vỏ tách ra khỏi hạt. Sau đó, đãi bỏ phần vỏ đậu xanh.#Bạn cho phần hạt sen đã sơ chế vào nồi cùng 800ml nước, bấm nút "cook". Khi nồi nhảy sang chế độ "warm" (giữ ấm), bạn dùng thìa vớt hết phần bọt trong nồi. Sau đó, cho đậu xanh cùng bó lá dứa vào nồi, khuấy đều và bấm nút "cook".#Sau khi nồi nhảy sang nút "warm", bạn hãy vớt bỏ phần lá dứa ra. Cho thêm 2 thìa cà phê bột năng, 1 thìa cà phê muối, 150-200gr đường kính và khuấy đều. Đậy nồi và để nguyên chế độ "warm" trong khoảng 5 phút.#Vậy là chị em đã hoàn thành xong món chè đậu xanh, hạt sen rồi đấy. Múc chè ra bát, thêm đá viên và thưởng thức thôi!\r\n\r\n', 'Hạt sen tươi/khô 70gr#Đậu xanh 150gr#Lá dứa 4-6 nhánh#Đường kính 150-200gr#Muối 1 thìa cà phê#Bột năng 2 thìa cà phê', '2021-06-14 19:49:08', '2021-06-14 19:49:08', 8, 3),
(24, '15 phút có ngay đĩa vải "Elsa băng giá" đang hot rần rần trên mạng, không thử món tráng miệng ngon "đỉnh của đỉnh" là tiếc lắm nhé!', 'vai-dong-da.jpg', 'Nguyên liệu đơn giản, cách làm chẳng cầu kỳ, thưởng thức lại ngon "đỉnh của đỉnh", chẳng trách món mới lại hot như vậy! "Món này thì mình thấy cách làm khá là dễ, mình làm xong thì cho vào ngăn đá để làm đông lại. Tuy nhiên khi bỏ ra ăn luôn, món này sẽ khiến nhiều bạn răng yếu cảm thấy buốt. Mọi người không quen ăn kiểu lạnh như vậy thì để 1 lúc hãy thưởng thức nhé" - Chị Linh chia sẻ.', 'Vải bóc vỏ, bỏ hột. Chị em tách khéo để quả vải còn nguyên vẹn, không bị rách phần cùi.#Khuấy đều hỗn hợp gồm sữa chua, sữa đặc, nước cốt dừa cho hòa quyện rồi rót vào trái vải.#Cho vào ngăn đá tối thiểu 4 tiếng trước khi ăn nhé!', 'Sữa chua 1 hộp# Sữa đặc 50ml# Nước cốt dừa 50ml', '2021-06-14 19:55:58', '2021-06-14 19:55:58', 16, 1),
(25, 'Mách chị em cách làm món tráng miệng thơm ngon chỉ cần 4 nguyên liệu: Từ người lớn đến trẻ con đều mê tít!', 'pudding-sua.jpg', 'Món tráng miệng mà chúng tôi gợi ý trong bài viết này đặc biệt phù hợp với các gia đình có trẻ nhỏ. Thông thường, các mẹ thường cho con ăn sữa chua hoặc váng sữa. Và nếu các bé đã bắt đầu có dấu hiệu ngấy 2 loại đồ tráng miệng trên thì cách làm pudding sữa này chắc chắn sẽ khiến cả bé lẫn ông bà, bố mẹ đều hào hứng đấy!\r\n\r\n', 'Cho vào bát: 100gr lòng trắng trứng cùng 30gr đường kính, dùng phới lồng đánh cho hỗn hợp hòa tan, tạo bọt sủi nhẹ như xà phòng.#Cho vào nồi: 1 muỗng canh nước, 50gr đường và đun trên ngọn lửa vừa, đồng thời dùng tay đảo đều nồi cho đến khi hỗn hợp nước đường tan, sôi lên và dần chuyển sang màu nâu cánh gián (màu caramel).#Tiếp theo, bạn cho vào 70gr heavy cream và dùng phới dẹt khuấy tan. Sau đó, cho 500ml sữa vào khuấy đều và đun trong khoảng 4-5 phút rồi tắt bếp. Kế tiếp chị em hãy lọc hỗn hợp qua rây lọc để phần kem sữa caramel được mềm mịn.#Đổ hỗn hợp pudding vào ly thủy tinh hoặc hộp thủy tinh, sử dụng giấy bạc bọc kín miệng ly/hộp.#Sau đó, cho ly/hũ pudding vào ngăn mát tủ lạnh.#Cho vào nồi 1 muỗng canh nước cùng 50gr đường, đun sôi nước đường trên lửa vừa cho đến khi hỗn hợp chuyển thành màu nâu cánh gián (màu caramel) thì tắt bếp.#Cuối cùng đổ vào 40ml nước và khuấy đều.#Múc 1 muỗng cà phê caramel đã nấu ở bước 4 lên và dàn đều bề mặt pudding, đợi khoảng 10-15 phút cho caramel đông lại.\r\n\r\n', 'Lòng trắng trứng 100gr (khoảng 4 quả trứng)#Đường kính 150gr#Heavy cream 150ml', '2021-06-14 20:06:08', '2021-06-14 20:06:08', 16, 3),
(26, 'HƯỚNG DẪN NẤU ĂN NGON\r\nChưa đầy 20 phút vào bếp, mỗi ngày đều mang món bún trộn này đi làm ăn trưa mà sau 2 tuần tôi giảm cả 3kg', 'banh-gao-lut.jpg', 'Trong bài viết này, chúng tôi sẽ gợi ý cho chị em cách làm món bún gạo lứt trộn vừa thơm ngon, vừa healthy để mang đi làm ăn trưa. Tình hình dịch bệnh đang diễn biến phức tạp nên lựa chọn an toàn nhất vẫn là "dắt túi" đồ ăn theo cùng.', 'Cà rốt, dưa leo bạn rửa sạch, nạo vỏ. Cắt dưa leo thành từng khoanh vừa ăn, bào cà rốt thành dạng sợi hoặc miếng mỏng.#Ớt chuông xanh, ớt chuông đỏ rửa sạch. Bạn bổ 2 loại ớt chuông làm đôi, bổ theo chiều dọc, bỏ phần cuống và hạt rồi cắt thành miếng dài khoảng 2-3 đốt ngón tay.#Bạn dùng dao khứa 1 đường theo chiều dọc của quả ớt sừng để loại bỏ hết phần hạt bên trong. Sau đó, cắt cuống ớt và băm nhỏ. Tỏi bóc vỏ rồi cũng băm nhỏ.#Tôm sau khi sơ chế, bạn cho vào nồi hấp hoặc luộc trong khoảng 15 phút. Đến khi thấy tôm ngả sang màu hồng thì tắt bếp. Đợi tôm nguội bớt rồi lột vỏ#Bạn cho khoảng 1.5 lít nước vào nồi, đun sôi. Khi nước sôi sùng sục, thả bún gạo lứt vào và trụng sơ trong khoảng 3 phút. Sau đó, vớt bún ra và lần lượt thả phần súp lơ, cà rốt và 2 loại ớt chuông vào trụng sơ trong khoảng 2 phút.#Bạn cho vào bát: 3 thìa canh nước sôi để nguội, 2 thìa cà phê đường, 1 thìa cà phê muối, 1.5 thìa cà phê nước mắm, 1 thìa canh giấm và khuấy đều cho các nguyên liệu tan vào nước.#Tiếp theo, bạn vắt lấy nước cốt 1 quả chanh và cho vào bát nước trộn cùng phần ớt sừng và tỏi đã băm nhỏ, khuấy đều 1 lần nữa.\r\n\r\n\r\n\r\n', 'Bún gạo lứt 30gr#Ớt chuông xanh, ớt chuông đỏ, cà rốt 1/2 quả mỗi loại#Ớt sừng, dưa chuột, chanh 1 quả mỗi loại#Ngọn súp lơ xanh hoặc trắng 1/2 cây#Tỏi 2-3 nhánh#Tôm 4-5 con', '2021-06-19 15:05:15', '2021-06-19 15:05:15', 14, 1),
(27, 'Mùa hè thì phải học ngay cách làm mì trộn lạnh chuẩn vị Hàn Quốc để ăn cho mát thôi!', 'mi-tron.jpeg', 'Bibim guksu là một món mì lạnh phổ biến được làm từ các loại rau tươi và có độ giòn ngon. Giống như bibimbap, nó rất linh hoạt trong việc kết hợp các nguyên liệu để ăn kèm mì. Việc bổ sung nhiều rau tươi trong công thức này giúp món ăn có lợi cho sức khỏe, thanh mát và sảng khoái! Kết quả là một món ăn vô cùng ngon miệng, hoàn hảo cho thời tiết mùa hè nóng nực.\r\n\r\n', 'Trộn tất cả các thành phần nước sốt và để trong ngăn mát tủ lạnh.#Thái nhỏ dưa chuột, cà rốt, bắp cải tím, rau xà lách xoăn. Giữ lạnh trong tủ lạnh hoặc trong nước đá (để ráo nước trước khi sử dụng) nếu bạn thích. Kim chi thái nhỏ.#Đun sôi một nồi nước. Luộc mì theo hướng dẫn trên bao bì.#Sau khi mì chín đổ ra rổ, xả nhanh dưới vòi nước lạnh cho mì thật nguội. Sau cuộn thành 2 vắt mì mỗi vắt là một khẩu phần ăn. Đặt 2 vắt mì lên rổ cho ráo nước.#Đặt mì vào giữa bát và sắp xếp các loại rau lạnh lên trên mì một cách hấp dẫn. Hãy thả một vài viên đá lạnh để món ăn luôn mát lạnh khi ăn. Đặt nửa quả trứng luộc lên trên bát. Nước sốt có thể được phục vụ trên cùng hoặc riêng biệt.\r\n\r\n', 'Mì kiều mạch 75g#Dưa chuột 1 quả#Cà rốt 1 củ nhỏ#Bắp cải tím 50g#Rau xà lách xoăn 50g#Kim chi 115g', '2021-06-19 15:07:41', '2021-06-19 15:07:41', 14, 1),
(28, 'Tối nào cũng dành 20 phút làm món này để mang đi làm ăn trưa, sau 1 tuần tôi giảm được gần 2kg!', 'goi-ga.gif', 'Thay vì ra ngoài ăn trưa hoặc gọi đồ ăn ship đến, chị em hãy tranh thủ vào bếp từ tối hôm trước và chuẩn bị món bún lứt trộn vô cùng healthy và thơm ngon mà chúng tôi gợi ý sau đây. Mang cơm đi làm vừa tiết kiệm, vừa giữ được an toàn cho bản thân trong bối cảnh covid-19 vẫn đang hoành hành thế này', 'Đầu tiên, bạn hãy rửa 100gr bún lứt với nước. Bắc 1 nồi nước lên bếp, đun sôi rồi thả bún vào trụng trong khoảng 2 phút. Sau đó, vớt bún ra và thả vào 1 âu nước đá lạnh để bún không bị nhão.#Rau cải tím, cà rốt, súp lơ và xà lách rửa sạch. Cải tím và cà rốt cắt sợi, rau súp lơ cắt miếng vừa ăn. Tiếp theo, lần lượt cho cải tím, cà rốt, súp lơ và ngô ngọt vào nồi nước vừa dùng để trụng bún và luộc sơ khoảng 2-3 phút. Sau đó, vớt ra để nguội.#Bạn cho vào bát: 1 thìa cà phê bơ lạc, 2 thìa cà phê xì dầu, 2 thìa cà phê nước cốt chanh, 2 thìa cà phê nước lọc, 1 thìa cà phê bột gừng, 1/2 thìa cà phê bột ớt, 1 thìa cà phê mật ong. Dùng thìa hoặc đũa khuấy đều cho các nguyên liệu tan vào nhau.#Bạn cho vào bát: 1 thìa cà phê bơ lạc, 2 thìa cà phê xì dầu, 2 thìa cà phê nước cốt chanh, 2 thìa cà phê nước lọc, 1 thìa cà phê bột gừng, 1/2 thìa cà phê bột ớt, 1 thìa cà phê mật ong. Dùng thìa hoặc đũa khuấy đều cho các nguyên liệu tan vào nhau.#Áp chảo ức gà, trụng bún và luộc sơ các loại rau củ, làm nước sốt, vậy là chị em đã hoàn thành xong món bún lứt trộn rồi đấy!', 'Ức gà 300gr#Bún lứt 100gr#Bơ lạc, bột gừng, bột ớt, mật ong 1 thìa cà phê mỗi loại#Xì dầu 2 thìa cà phê#Nước cốt chanh 2 thìa cà phê#Rau xà lách 2 lá to#Cà rốt, ngô ngọt, bắp cải tím, súp lơ xanh 10gr mỗi loại', '2021-06-19 15:10:03', '2021-06-19 15:10:03', 14, 1),
(29, 'Tạm quên cách nấu lẩu nấm quen thuộc đi, gợi ý cho bạn cách nấu lẩu nấm của người Hàn cũng rất đáng học hỏi!', 'lau-nam.jpeg', 'Bữa ăn cuối tuần thịnh soạn này là một cách tuyệt vời để thưởng thức các loại nấm tốt cho sức khỏe với hương vị đậm đà và thanh ngọt. Chúng chứa nhiều chất xơ, protein, vitamin, khoáng chất và một nguồn chất chống oxy hóa dồi dào. Đây cũng là một trong những món ăn mà bạn thực sự không cần số lượng chính xác cho các nguyên liệu, vì vậy công thức chỉ đóng vai trò là hướng dẫn.', 'Củ cải gọt vỏ cắt khúc; rong biển rửa sạch, cắt miếng. Nấm rửa sạch; hành tây bóc vỏ. Cho 1 lít nước vào nồi, cho củ cải, rong biển, hành tây, nấm vào. Sau đó đun sôi và giảm nhỏ lửa, ninh trong vòng 30 phút cho ngọt nước. Nêm nước tương, muối và hạt tiêu. Khi nước dùng đã ngọt thì vớt phần rau củ này ra khỏi nồi.#Trước khi ăn hãy rửa nấm, không nên rửa sớm quá sẽ làm mất chất. Nấm không nên thái miếng mỏng quá vì nó nhanh chín. Rau nhặt sạch lá úa, rửa sạch rồi cắt khúc vừa ăn sau đó để cho ráo nước.#Sắp xếp gọn gàng nấm và hành lá thành từng cụm cùng với các loại rau khác trong nồi lẩu. Sau đó thêm nước dùng vào. Đun với lửa to cho nấm chín trong khoảng 4 - 5 phút. Bạn có thể thêm nước dùng nếu cạn và thêm các nguyên liệu khác trong khi ăn.#Món lẩu nấm thanh ngọt, nóng hổi cho cả nhà đổi vị cuối tuần thì còn gì tuyệt hơn.', '1/2 củ hành tây #1 cây củ cải trắng to#1 miếng rong biển khô dùng để nấu canh#Vài cây nấm hương#Các loại nấm (nút, hương, sò, đùi gà, kim châm, hải sản...)#2 lá cải thảo lớn#1 củ cà rốt thái lát mỏng#Đậu phụ#Miến dong Hàn Quốc', '2021-06-19 15:13:59', '2021-06-19 15:13:59', 18, 1),
(30, 'Trời lạnh thế này làm nồi lẩu bò nhúng me xì xụp là tuyệt nhất!', 'lau-bo.jpg', 'Làm những món bún rồi bắc nồi nước dùng nóng để nhúng rau là rất "chuẩn bài" cho những ngày rét run thế này. Ở nhà cuối năm sắp Tết, các bạn cũng có thể tham khảo những món dễ làm và dễ ăn dù nhiều thịt mà không hề ngán như món lẩu bò nhúng me này nhé.', 'Lọc mắm nêm qua rây cho mịn. Bắc chảo, làm nóng dầu ăn cho một nửa số tỏi, sả băm vào phi vàng, tiếp theo cho mắm nêm và nước vào đảo đều và đun cho mắm sôi, vớt váng, thêm đường và dứa băm nhuyễn vào đun cho tan hết đường thì tắt bếp, để nguội thêm nước cốt chanh và ớt vào, nêm nếm lại cho vừa khẩu vị.#Cho me chín vào bát, thêm vào 200ml nước sôi, dầm cho me tan ra rồi lọc qua rây lấy nước cốt.#Bắc nồi nhỏ, cho phần nước cốt me và đường vào đun cho tan hết đường rồi thêm vào 30ml nước mắm. Đun lửa nhỏ cho đến khi nước mắm keo lại thì thêm tỏi và ớt vào. Giã lạc rang rắc lên trên.#Tỏi, hành khô băm nhỏ, bắc chảo, làm nóng dầu ăn cho dứa và hành, tỏi băm nhỏ vào phi thơm rồi cho phần nước dừa tươi vào, thêm cốt me. Nêm nếm vào đường, muối, nước mắm cho vừa khẩu vị chua ngọt. Nhưng sẽ hơi chua một chút thì mới kích thích khẩu vị và giảm ngán từ thịt khi ăn.#Phần thịt bò thái mỏng, trộn đều với hành tây đã thái mỏng, một chút dầu mè và chút muối, bóp đều rồi xếp ra đĩa. Rắc mè rang lên trên và trang trí những khoanh hành tây đã thái tròn.#Bắc nồi lẩu, múc phần nước dùng vào, thêm hành tây thái khoanh tròn và rắc ít mè rang lên trên. Khi ăn thêm lạc rang và ớt vào, nước sôi thả đậu, bò viên, giò tai, nhúng thịt bò và rau chấm mắm nêm hoặc mắm me ăn kèm bún. Vị chua dịu ngọt và thơm nước dừa cùng dứa, hơi béo bùi của lạc rang và mè rang rất dễ ăn, không bị ngán.', '50g mắm nêm Dì Cẩn nguyên chất#5 tép tỏi băm nhỏ#1/2 quả chanh vắt lấy nước cốt#1/2 cây sả băm nhỏ#1 miếng dứa bằm nhỏ#2 thìa cơm đường#20g me chín (hoặc sử dụng cốt me Thái Lan)#40g đường cát vàng', '2021-06-19 15:17:12', '2021-06-19 15:17:12', 18, 1),
(31, 'Mai là ngày mùng 1 rồi, xem ngay cách làm chả chay của Food Blogger Liên Ròm ăn ngon hết cỡ thôi!', 'cha-ram.png', 'Chả chay ăn với bún, bánh hỏi hay cơm đều cực ngon, mai là mùng 1 rồi bạn nhớ làm ngay nhé!', 'Cho bột vào thố cùng gia vị và rong biển, chế nước sôi. Trộn đều và để trong tủ lạnh 30 phút - 45 phút.#Lấy ra và hấp cho chín trong vòng 20 phút#Ướp gia vị lên phần mỳ căn đã hấp, để cho ngấm trong vòng nửa tiếng và đem đi áp chảo hai mặt#Xắt miếng vừa ăn. Có thể ăn chung bánh mỳ, cơm hay cuốn rau cùng bánh hỏi, bún.Nếu các bạn không có bột mỳ căn thì dùng bột mỳ và tự giặt bột. Sau khi có mỳ căn rồi thì các bạn xé nhỏ đem đi xay và cho gia vị từ từ vô, hoặc các bạn đem đi giã cho gia vị thấm vào mỳ căn, sau đó hấp và áp chảo nhé!', 'Bột mỳ căn (vital wheat gluten flour) 120g#Bột đậu gà (chickpea flour) 30g#Tiêu, dầu mè mỗi thứ 1 muỗng cafe#Muối 1/2 muỗng cafe#Rong biển sấy giòn 30g#Phần nước sốt ướp: 1/2 muỗng ớt bột, 1 muỗng canh syrup hay mật ong, 1/2 muỗng cà phê bột nghệ, 1 muỗng canh nước tương, 1 muỗng canh dầu hào chay', '2021-06-19 15:21:05', '2021-06-19 15:21:05', 6, 1),
(32, 'Tết Hàn Thực nếu ngán bánh trôi bánh chay, hãy thử món này - ngon đẹp lung linh bảo sao khiến hội chị em rần rần thích thú!', 'che-khoai-deo.jpg', 'Tết Hàn Thực thì không thể thiếu bánh trôi, bánh chay nhưng nếu đã ăn hai món này quá nhiều thì các chị em thử đổi món với chè khoai dẻo xem sao?', 'Khoai sau khi gọt vỏ, các bạn rửa sạch cho hết nhựa và cắt bỏ 2 đầu vì đây là nơi có nhiều xơ cứng. Sau đó cắt khoai thành những khoanh dày chừng 1,5cm. #Đun sôi nước và cho khoai vào xửng hấp to lửa khoảng 10 - 15 phút. Kiểm tra thấy khoai chín bở là được.#Khoai cần nhào luôn khi nóng để có độ kết dính nên các bạn lấy khoai ra thì bỏ vào một cái bát to, cho bột năng vào cùng khoai rồi nhanh tay dùng dĩa dầm nhuyễn. Lúc này nhìn hỗn hợp khoai và bột sẽ có cảm giác tơi ra, các bạn cho nước vào rồi dùng tay nhào cho hỗn hợp khoai trở thành một khối đồng nhất. #Kiểm tra thấy bột mịn, dẻo, vê tròn hay tạo hình không thấy bột bị nứt là được. Nếu thấy bột nứt chứng tỏ bột hơi khô thì lại thêm chút nước vào nhào tiếp.#Sau khi đã có các phần khoai dẻo với màu sắc như ý thì các bạn tạo hình tùy vào sự sáng tạo của mình.#Để luộc khoai dẻo các bạn chuẩn bị nồi nước đầy một chút. Đun sôi rồi nhẹ nhàng thả các viên khoai dẻo vào nồi luộc, nên luộc màu sáng trước rồi mới luộc màu khoai tím.#Chè khoai dẻo thường sẽ ăn cùng thạch sương sáo và nước đường gừng (hỗn hợp gồm: nước + đường thốt nốt + vài lát gừng + lá nếp đun sôi)', 'Khoai lang vàng và tím đã gọt vỏ 600g#Bột năng 240g#Nước 80 - 100ml', '2021-06-19 15:24:01', '2021-06-19 15:24:01', 6, 1),
(33, 'Cuối tháng "cầm hơi" chờ lương mà vẫn muốn ăn uống sang chảnh: Pizza mì tôm sẽ giải cứu cái miệng khó chiều của chị em!', 'pizza-mi-tom.jpg', 'Cuối tháng rồi, nếu chiếc ví của bạn đang "hấp hối" chờ được hô hấp nhân tạo, còn cái miệng lại cứ thèm ăn sang, món pizza mì tôm mà chúng tôi gợi ý sau đây có thể sẽ giúp chị em tạm thời giải tỏa được mong muốn lắt léo này!', 'Chần mì tôm với nước sôi sùng sục trong khoảng 90 giây để mì mềm và tơi. Sau đó, vớt mì ra để nguội.#Thanh cua, xúc xích cắt nhỏ. Hành lá, ngò rí rửa sạch và cũng cắt nhỏ. Đập 1 quả trứng gà ra bát, cho thanh cua, xúc xích, hành lá, ngò rí vào cùng 1 muỗng cà phê hạt nêm, 1/2 muỗng cà phê đường, 1/2 muỗng cà phê ớt bột và 1 muỗng cà phê tương ớt và đánh đều.# Làm nóng chảo với 1 muỗng canh dầu ăn rồi cho mì, dàn đều trên mặt chảo chiên đến khi giòn thì lật mặt chiên thêm 4 phút.#Khi mì đã chín giòn, rưới đều phần hỗn hợp trứng, thanh cua, xúc xích lên trên mặt mì tôm. Đậy nắp chảo và chiên thêm 5 phút. Vậy là chị em đã hoàn thành xong món pizza mì tôm rồi! Đổ "pizza" ra đĩa, cắt thành từng miếng tam giác như pizza nữa là hoàn thành!', '1 gói mì tôm#1 quả trứng gà, 1 chiếc xúc xích, 2-3 chiếc thanh cua#15gr hành lá, 15gr ngò rí#Gia vị: Hạt nêm, tương ớt, đường, ớt bột, dầu ăn', '2021-06-19 15:27:02', '2021-06-19 15:27:02', 9, 1),
(34, 'Pizza không tinh bột: Chỉ mất 30 phút chế biến, chị em đã có thể ăn đẫy miệng mà chẳng lo phá vỡ lộ trình ăn kiêng!', 'pizza-rau.jpeg', '30 phút vào bếp mà có ngay bánh ngon để ăn trong gần 1 tuần liền, tội gì mà không làm nhỉ các chị em ơi!', 'Cho 225gr (tương đương 2 bát) phô mai mozzarella cắt nhỏ và 28gr (tương đương 2 thìa canh) kem phô mai vào trong một chiếc bát có thể dùng trong lò vi sóng. Cho bát phô mai và kem phô mai vào lò vi sóng trong khoảng 90 giây ở mức nhiệt Trung bình - cao để làm tan chảy.#Thêm 3/4 bát bột hạnh nhân (hoặc 1/3 bát bột dừa) và 1 quả trứng vào trong bát phô mai đã tan chảy, khuấy cho đến khi các nguyên liệu quện vào nhau. Sau đó, xoa một chút dầu olive lên bàn tay của bạn để chống dính và nhào bột cho tới khi bột mịn và có độ nhão, vê tròn lại.#Sau khi bột đã được nhào mịn, để bột lên trên giấy nến rồi ấn xẹp, cán bột thành hình tròn có đường kính khoảng 25cm. Bạn có thể dùng dĩa chọc vào lòng bánh để tránh bị phồng khi nướng.#Nướng đế bánh ở mức nhiệt 400 độ F (khoảng 204 độ C) trong vòng 8 phút. Nếu sử dụng nồi chiên không dầu thay vì lò nướng, bạn có thể cài mức nhiệt 200 độ trong 4 phút, sau đó lấy đế bánh ra và lật mặt, tiếp tục nướng ở nhiệt độ như trên với mặt còn lại.', '225gr (tương đương 2 bát) phô mai mozzarella cắt nhỏ#28gr (tương đương 2 thìa canh) kem phô mai #3/4 bát bột hạnh nhân hoặc 1/3 bát bột dừa#1 thìa canh dầu olive#1 quả trứng#1 bát phô mai Parmesan nghiền nhỏ#1/2 bát heavy cream#1/2 bát phô mai mozzarella cắt nhỏ', '2021-06-19 15:30:31', '2021-06-19 15:30:31', 9, 1),
(35, 'Quên nước ép và sinh tố dưa hấu đi, mùa hè này hãy làm pizza dưa hấu ăn vừa mịn da lại đẹp dáng thôi!', 'pizza-dua-hau.jpg', 'Với sự kết hợp từ dưa hấu, sữa chua và các loại trái cây khác của mùa hè, bạn sẽ có món pizza dưa hấu siêu ngon mà làm thì dễ vô cùng!', 'Bạn chọn những quả dưa hấu tươi ngon, đem về rửa sạch và cắt thành từng lát vừa ăn, vẫn để nguyên vỏ cho nhanh hoặc bỏ vỏ nếu bạn có thời gian.#Phết sữa chua thực vật plant-based yogurt (không làm từ sữa bò) như yogurt đậu nành, yogurt sữa dừa nếu bạn ăn thuần chay và kiêng các sản phẩm từ động vật. Nếu bạn theo chế độ ăn bình thường thì dùng sữa chua ít đường hoặc không đường là tốt nhất.#Phủ topping bằng các loại hạt để cung cấp chất béo như hạt bí, hạt hướng dương, hạnh nhân… và thêm các loại trái cây tươi có vị chua như dâu tây, việt quất, mâm xôi. Thêm chút cơm dừa già nếu bạn muốn cảm giác giòn giòn và béo ngậy nhé!', 'Dưa hấu tùy lượng dùng#Sữa chua#Các loại hạt như hạt bí, hạt hướng dương, hạnh nhân#Các loại trái cây tươi có vị chua như dâu tây, việt quất, mâm xôi', '2021-06-19 15:33:55', '2021-06-19 15:33:55', 9, 1),
(36, 'Nước mía thì ai cũng biết nhưng kết hợp cùng loại quả này sẽ tạo ra món sinh tố "gây nghiện" hơn cả trà sữa!', 'sinh-to-mia.gif', 'Nước mía lạnh chắc hẳn là thức uống yêu thích của không ít chị em trong những ngày nắng nóng. Loại nước này uống không thôi đã thấy ngon lắm rồi, nhưng trong bài viết này, chúng tôi sẽ gợi ý cho bạn cách kết hợp nước mía với sầu riêng, thạch lá dứa để cho ra đời món sinh tố ngon không lối thoát!', 'Bạn đeo găng tay vào và tách phần cơm sầu riêng khỏi hạt rồi cho phần cơm sầu tách được vào bát, dằm thật nhuyễn.#Bạn cũng tách bỏ phần hạt của 100gr mít, sau đó xé mít thành từng sợi nhỏ. Lá dứa rửa sạch, để ráo và cắt thành từng khúc dài khoảng 5-7cm.#Bạn cho phần lá dứa đã cắt nhỏ vào máy xay sinh tố cùng 300ml nước dừa tươi và bấm máy xay nhuyễn. Sau đó, lọc hỗn hợp thu được qua rây lọc để loại bỏ phần bã.#Bạn đổ hỗn hợp nước vừa lọc vào nồi, thêm 5gr bột rau câu dẻo và 20gr đường kính vào nồi. Vừa đun vừa khuấy đều cho đến khi hỗn hợp sôi lăn tăn thì tắt bếp. Đổ hỗn hợp thu được vào hộp thủy tinh và để nguội hoàn toàn.#Bạn cho 100gr cơm sầu riêng đã dằm nhuyễn vào máy xay sinh tố cùng 500ml nước mía, bấm máy xay nhuyễn.#Đầu tiên, bạn hãy đổ phần thạch lá dứa đã để nguội ra thớt và thái thạch thành từng miếng vừa miệng.#Tiếp theo, bạn cho vài viên đá vào cốc, rót phần sinh tố nước mía - sầu riêng vừa xay vào rồi thêm thạch lá dứa cùng cùi dừa nạo và lạc rang lên trên (nếu có). Vậy là xong!\r\n\r\n', ' Nước mía 500ml#Nước dừa tươi 300ml#Sầu riêng, mít dai 100gr mỗi loại#Lá dứa 5-7 nhánh#Bột rau câu dẻo 5gr#Đường kính 20gr#Cùi dừa, lạc rang (không bắt buộc) 1 ít', '2021-06-19 15:37:22', '2021-06-19 15:37:22', 11, 1),
(37, 'Tuyệt chiêu pha trà sữa mới toanh cho mùa hè 2021: Uống vào đảm bảo mê ngay!', 'tra-sua-dau.jpg', 'Trà sữa là sự kết hợp giữa trà và sữa tươi hoặc sữa dừa. Tùy theo lượng sữa, lượng trà cùng các loại topping khác nhau như bột báng, hay trái cây xay nhỏ như dâu tây mà mỗi ly trà sữa chứa lượng dinh dưỡng cũng như calo khác nhau. Mặt khác, trong trà sữa chứa caffeine, giúp cơ thể tỉnh táo. Tuy nhiên, bạn không nên uống trà sữa vào buổi tối, thức uống này có thể khiến bạn mất ngủ đấy!', 'Cho gói trà túi lọc vào nồi, thêm 200ml nước lọc và đường vào nấu sôi trong 2-3 phút rồi để nguội.#Bỏ túi lọc trà rồi đổ 100ml nước trà vào khay làm đá viên sau đó đặt vào ngăn đá tủ lạnh để đông đá. 100ml nước trà còn lại thì để vào ngăn mát tủ lạnh#Khi trà đã đông đá thì bắt đầu xay dâu tây, cho dâu tây vào máy xay sinh tố, thêm 100ml nước trà vào rồi xay nhuyễn.#Cho 2 thìa bột báng đã luộc chín vào ly. Thêm dâu tây đã xay vào. Lấy viên trà đá cho vào ly dâu tây. Cuối cùng đổ sữa dừa lên trên là xong.', 'Nước cốt dừa 100g#Trà túi lọc 1 túi#Dâu tây đông lạnh 8 trái#Bột báng luộc chín 2 muỗng canh', '2021-06-19 15:40:52', '2021-06-19 15:40:52', 9, 1),
(38, 'Cuối tuần rảnh rang, tranh thủ vào bếp làm ly trà sữa full topping uống cho đã!', 'cach-pha-tra-sua-ngon.jpg', 'Hè rồi, trà sữa chắc hẳn là một trong những món đồ uống yêu thích của không ít chị em. Nếu bạn vừa muốn tiết kiệm, vừa muốn hạn chế tối đa lượng đường và chất béo của trà sữa, vậy thì cách duy nhất là phải tự pha thôi!\r\n\r\n', 'Cho vào bát: 5gr bột gelatin cùng 2 muỗng canh nước, khuấy đều rồi ngâm 15 phút cho bột nở. Tiếp theo, chị em đổ khoảng 450ml nước sôi 100 độ C vào nồi, thả 2 gói trà túi lọc vào và ngâm trong khoảng 2-3 phút.#Sau khi trà tan ra, bạn hãy cho 2 muỗng sữa đặc cùng hỗn hợp bột gelatin đã ngâm ban nãy vào nồi nước trà rồi khuấy đều.#Cho 5gr bột rau câu dẻo vào bát, trộn đều với 80gr đường. Sau đó, bắc nồi, đun sôi 1 lít nước lọc, đợi khi nước sôi bạn đổ hỗn hợp rau câu vào và khuấy đều tay để phần bột không bị vón cục, tiếp tục đun cho đến khi nước chuyển sang sánh.#Cuối cùng, cho 1 muỗng canh siro vào nồi để tạo màu cho thạch. Vừa cho, vừa khuấy đều trong khoảng 1 phút. Sau đó, tắt bếp, đợi thạch nguội và đông lại trong khoảng 25 phút thì cắt thành khối vuông hay thành dạng sợi đều được.', 'Bột gelatin 5gr#Sữa đặc 2 muỗng canh#Trà túi lọc 2 gói#Đường kính 80gr#Bột rau câu dẻo 5gr', '2021-06-19 15:43:06', '2021-06-19 15:43:06', 9, 1);
